        dots: true,
        infinite: true,
        slidesToScroll: 1,
        nextArrow: <SampleNextArrow className="SampleNextArrow" />,
        prevArrow: <SamplePrevArrow className="SamplePrevArrow" />,
        responsive: [

        {
          breakpoint: 600,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
            arrows: true,
            autoplay: true,

