/**
 * Gives a CSS custom property value applied at the element
 * element {Element}
 * varName {String} without '--'
 *
 * For example:
 * readCssVar(document.querySelector('.box'), 'color');
 */
export declare function readCssVar(element: HTMLElement, varName: string): string;
/**
 * Writes a CSS custom property value at the element
 * element {Element}
 * varName {String} without '--'
 *
 * For example:
 * readCssVar(document.querySelector('.box'), 'color', 'white');
 */
export declare function writeCssVar(element: HTMLElement, varName: string, value: string): void;
/**
 * Writes multiple CSS custom property values on the element
 * element {Element}
 * varName {String} without '--'
 *
 * For example:
 * readCssVar(document.querySelector('.box'), 'color', 'white');
 */
export declare function updateStyles(element: HTMLElement, customProperties: any): boolean;
